import React from 'react';
import Url from 'url-parse';

class SocialMedia extends React.Component {

  constructor(props) {
    super(props);

    this.parseUrl = this.parseUrl.bind(this);
    this.formatUrl = this.formatUrl.bind(this);
  }


  formatUrl(url) {
    let newUrl = new Url(url);
    let trimmedUrl;
    let subString = "/artists/";

    if (newUrl.pathname === "/artists/null") {
      trimmedUrl = 'null';
    } else if (newUrl.pathname.includes("/artists/")) {
      trimmedUrl = newUrl.pathname.replace(subString, '');
    }

    return trimmedUrl;
  }

  parseUrl(url) {
    let trimmedUrl = formatUrl(url);

    let finalUrl = trimmedUrl.split('/');
    // .replace(/^\/|\/$/g, '');

    return finalUrl[1];
  }

  render () {
    console.log("socialmedia", this.props);

    return (
      <div className="socialMediaContainer">
        {this.props.facebook !== "null" ?
          (
          <p>
            <i className="fab fa-facebook-f"></i>
            <a href={this.formatUrl(this.props.facebook)} target="_blank" rel="noopener noreferrer"> {this.parseUrl(this.props.facebook)}</a>
          </p>) : ''}
        {this.props.twitter !== "null" ?
        (
        <p>
          <i className="fab fa-twitter"></i>
          <a href={this.props.twitter} target="_blank" rel="noopener noreferrer"> {this.parseUrl(this.props.twitter)}</a>
        </p>) : ''}
        {this.props.instagram !== "null" ?
          (
          <p><i className="fab fa-instagram"></i>
            <a href={this.props.instagram} target="_blank" rel="noopener noreferrer">{this.parseUrl(this.props.instagram)}</a>
          </p>) : ''}
      </div>
      )
  }
}

export default SocialMedia;
