import React from 'react';
import axios from 'axios';

class StarPhotographer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {liked: false};

    this.handleLikeClick = this.handleLikeClick.bind(this);
    this.handleUnlikeClick = this.handleUnlikeClick.bind(this);
  }

  handleLikeClick() {
    axios.post('/artists/:id/like', { currentUser: this.props.currentUser })
      .then((res) => {
        this.setState({liked: true, numberOfLikes: res.data.length});
    });
  }

  handleUnlikeClick() {
    axios.post('/artists/:id/unlike', { currentUser: this.props.currentUser })
      .then((res) => {
        this.setState({liked: false, numberOfLikes: res.data.length});
    });
  }

  render() {
    return(
      this.state.liked ? (
        <div>
          <i className="fas fa-star" onClick={this.handleUnlikeClick}></i>
          <p>{this.state.numberOfLikes}</p>
        </div>
        ) : (
        <div>
          <i className="far fa-star" onClick={this.handleLikeClick}></i>
          <p>{this.state.numberOfLikes}</p>
        </div>)
      )
    }
}

export default StarPhotographer;
