import React, {Component} from 'react';
import SeeAvailability from './SeeAvailability.jsx';
import Portfolio from './Portfolio.jsx';
import Avatar from './components/Avatar.jsx';
import Slider from "react-slick";
import Link from 'react-router-dom';
import axios from 'axios';

class ProfileDesc extends React.Component {
  render() {
    return (
      <div className="profiledesc">
        <p>{this.props.bio}</p>
      </div>
    )
  }
}

// https://blog.campvanilla.com/reactjs-dropdown-menus-b6e06ae3a8fe
class AvailabilityCard extends React.Component {
  constructor() {
    super();

    this.state = {
      showMenu: false,
    };

    this.showMenu = this.showMenu.bind(this);
    this.closeMenu = this.closeMenu.bind(this);
  }

  showMenu(event) {
    event.preventDefault();

    this.setState({ showMenu: true }, () => {
      document.addEventListener('click', this.closeMenu);
    });
  }

  closeMenu(event) {

    if (!this.dropdownMenu.contains(event.target)) {

      this.setState({ showMenu: false }, () => {
        document.removeEventListener('click', this.closeMenu);
      });

    }
  }

  render() {
    return (
      <div className="profilebtn">
        <button onClick={this.showMenu}>
          Availability
        </button>

        {
          this.state.showMenu
            ? (
              <div
                className="menu"
                ref={(element) => {
                  this.dropdownMenu = element;
                }}
              >
                <SeeAvailability />
              </div>
            )
            : (
              null
            )
        }
      </div>
    );
  }
}

// https://blog.campvanilla.com/reactjs-dropdown-menus-b6e06ae3a8fe
class PackagesCard extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      showMenu: false,
    };

    this.showMenu = this.showMenu.bind(this);
    this.closeMenu = this.closeMenu.bind(this);
    this.renderPricePackage = this.renderPricePackage.bind(this);
  }

  // renderPricePackage(pricepackages=[]) {
  //   let tier;
  //   let tierPrice;
  //   return pricepackages.map(function(pricepackage) {
  //     if (pricepackage.tier === 1) {
  //     tier = "Basic";
  //     tierPrice = pricepackage.price;
  //   } else if (pricepackage.tier === 2) {
  //     tier = "Intermediate";
  //     tierPrice = pricepackage.price;
  //   } else if (pricepackage.tier === 3) {
  //     tier = "Deluxe";
  //     tierPrice = pricepackage.price;
  //   }
  //     return (
  //       <div>
  //         { tier ? (<div><h5>{tier}</h5><br/><p>{tierPrice}</p></div>) : ""}
  //       </div>
  //       )
  //   })

  // }

  showMenu(event) {
    event.preventDefault();

    this.setState({ showMenu: true }, () => {
      document.addEventListener('click', this.closeMenu);
    });
  }

  closeMenu(event) {

    if (!this.dropdownMenu.contains(event.target)) {

      this.setState({ showMenu: false }, () => {
        document.removeEventListener('click', this.closeMenu);
      });

    }
  }

  render() {
    console.log("this is packages from server", this.props.packages);
    return (
      <div className="profilebtn" >
        <button onClick={this.showMenu}>
          Packages
        </button>

        {
          this.state.showMenu
            ? (
              <div
                className="menu"
                ref={(element) => {
                  this.dropdownMenu = element;
                }}
              >
              {this.renderPricePackage(this.props.packages)}
              </div>
            )
            : (
              null
            )
        }
      </div>
    );
  }
}

class Profile extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      artist: {}
    }
    this.addCarouselPhotos = this.addCarouselPhotos.bind(this);
    this.areFeaturedPhotos = this.areFeaturedPhotos.bind(this);
  }

  areFeaturedPhotos(photos=[]) {
    return photos.filter(photo => photo.featured.includes("true"));
  }

  addCarouselPhotos(photos=[]) {
    let filteredPhotos = this.areFeaturedPhotos(photos);
    return filteredPhotos.map(function(photo) {
      return (
        <div className="sliderImg" >
          <img alt="900x500" src={photo.src} />
        </div>
        )
    });
  }

  componentWillMount() {

    const { id } = this.props.match.params;

    axios.get(`/artists/${ id }`, {
      params: {
        artistId: id
      }
    })
    .then((res) => {
      console.log("artist profile query, app.js", res.data);
      let collection = res.data;
      let bio = res.data[0].bio;
      let fullName = res.data[0].first_name + ' ' + res.data[0].last_name;
      let avatarImage = res.data[0].profile_image;
      this.setState({redirect: true, fullName: fullName, avatarImage: avatarImage, collection: collection, bio: bio});
    });

      // axios.get(`/artists/${ id }`)
      // .then(artist => {
      //   debugger;
      // })
  }

  render() {
    const settings = {
      infinite: true,
      centerMode: true,
      slidesToShow: 2,
      slidesToScroll: 1,
      rows: 1,
      autoplay: true,
      focusOnSelect: true,
    };

  return (

  <div className="profile">
    <Avatar name={ this.state.fullName } avatar={ this.state.avatarImage }/>
    {<ProfileDesc bio={this.state.bio}/>}
    <div className="featuredPortfolio">
      <h1>Featured Photos:</h1>
      <Slider {...settings} >
        {this.addCarouselPhotos(this.state.collection)}
      </Slider>
      <br />
      {/*<a href="/portfolio"><h5>See full portfolio</h5></a>*/}
    </div>
      <Portfolio />
    <AvailabilityCard />
    <PackagesCard packages={this.state.collection}/>
  </div>
  );
 }
}

export default Profile;
