import React from 'react';
import Splashart from './Splashart.jpg';
import Carousel from 'react-bootstrap/lib/Carousel';

class Home extends React.Component {

  constructor(props) {
    super(props);

    this.categorizePhotos = this.categorizePhotos.bind(this);
    this.addCarouselPhotos = this.addCarouselPhotos.bind(this);
  }

  categorizePhotos(category, photos) {
    console.log(photos);
    let newArray = [];
    photos.forEach(function(photo) {
      if (photo.category.includes(category)) {
        newArray.push(photo);
      }
    });
    console.log("this is new array on home", newArray);
    return newArray;
  }

  addCarouselPhotos(photos) {
    photos.map(function(photo) {
      return (
        <Carousel.Item>
          <img alt="900x500" src={photo.src} />
        </Carousel.Item>
        )
    });
  }

  componentDidMount () {
    console.log("this is data from app", this.props.homecategory);
    let categorizedPhotos = this.categorizePhotos("wedding", this.props.homecategory);
    let carouselPhotos = this.addCarouselPhotos(categorizedPhotos)
  }

  render() {


  return (

    <div>
      <div className="splashcontainer">
        <img className="splashart" alt="splash" src={Splashart} />
      </div>
      <Carousel>
        {carouselPhotos}
      </Carousel>
    </div>
  );
 }
}

export default Home;
