import React from 'react';
import DayPicker from 'react-day-picker';
import axios from 'axios';

import 'react-day-picker/lib/style.css';

class EditAvailability extends React.Component {
  constructor(props) {
    super(props);
    this.handleDayClick = this.handleDayClick.bind(this);
    this.state = {
      selectedDay: undefined,
      disabledDays: undefined
    };
  }
  handleDayClick(day) {
    this.setState({ selectedDay: day }, console.log("this is avail.jsx", day));
    axios.post('/artists/:id/availability', {selectedDay: day, artistId: this.props.currentUser});

  }

  componentWillMount() {
    axios.get('/testing', {
      params: {
        artistId: this.props.currentUser
      }
    })
     .then((res) => {
        this.setState({disabledDays: res.data});
      });
  }

  render() {
    console.log("this is in render of avail.jsx", this.state.disabledDays);
    return (
      <div>
        <DayPicker
          onDayClick={this.handleDayClick}
          selectedDays={this.state.selectedDay}
        />
        {this.state.selectedDay ? (
          <p>You clicked {this.state.selectedDay.toLocaleDateString()}</p>
        ) : (
          <p>Please select a day.</p>
        )}
      </div>
    );
  }
}

