import React, {Component} from 'react';
import EditAvailability from './components/Availability.jsx';
import Portfolio from './components/Portfolio.jsx';
import Avatar from './components/Avatar.jsx';
import Slider from "react-slick";
import { Link } from 'react-router-dom';
import axios from 'axios';
import EditProfile from './EditProfile';
import SocialMedia from './components/SocialMedia.jsx';
import './styles/Profile.css';

class ProfileDesc extends React.Component {
  render() {
    return (
      <div className="profiledesc">
        <p>{this.props.bio}</p>
      </div>
    )
  }
}



// https://blog.campvanilla.com/reactjs-dropdown-menus-b6e06ae3a8fe
class AvailabilityCard extends React.Component {
  constructor() {
    super();

    this.state = {
      showMenu: false,
    };

    this.showMenu = this.showMenu.bind(this);
    this.closeMenu = this.closeMenu.bind(this);
  }

  showMenu(event) {
    event.preventDefault();

    this.setState({ showMenu: true }, () => {
      document.addEventListener('click', this.closeMenu);
    });
  }

  closeMenu(event) {

    if (!this.dropdownMenu.contains(event.target)) {

      this.setState({ showMenu: false }, () => {
        document.removeEventListener('click', this.closeMenu);
      });

    }
  }

  render() {
    return (
      <div className="profilebtn">
        <button onClick={this.showMenu}>
          Availability
        </button>
        { this.state.showMenu ? (
          <div className="menu"
            ref={ (element) => { this.dropdownMenu = element } }>
            <EditAvailability artistId={this.props.artistId}
                              disabledDays={this.props.disabledDays}/>
          </div>
          ) : (
          null )
        }
      </div>
    );
  }
}

// https://blog.campvanilla.com/reactjs-dropdown-menus-b6e06ae3a8fe
class PackagesCard extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      showMenu: false,
    };

    this.showMenu = this.showMenu.bind(this);
    this.closeMenu = this.closeMenu.bind(this);
    this.renderPricePackage = this.renderPricePackage.bind(this);
  }

  renderPricePackage(pricePackages=[]) {
    let tier;

    return pricePackages.map(function(pricePackage) {
      if (pricePackage.tier === 1) {
      tier = "Basic";
    } else if (pricePackage.tier === 2) {
      tier = "Intermediate";
    } else if (pricePackage.tier === 3) {
      tier = "Deluxe";
    }
      return (
        <div>
          <h5>{tier}</h5>
          <p>{pricePackage.price}</p>
        </div>
        )
    })

  }

  showMenu(event) {
    event.preventDefault();
    this.setState({ showMenu: true }, () => {
      document.addEventListener('click', this.closeMenu);
    });
  }

  closeMenu(event) {
    if (!this.dropdownMenu.contains(event.target)) {
      this.setState({ showMenu: false }, () => {
        document.removeEventListener('click', this.closeMenu);
      });
    }
  }

  render() {
    return (
      <div className="profilebtn" >
        <button onClick={ this.showMenu }>
          Packages
        </button>
        {this.state.showMenu ? (
          <div className="menu"
            ref={ (element) => { this.dropdownMenu = element } }>
            {this.renderPricePackage(this.props.packages)}
          </div>
          ) : (
          null )}
      </div>
    );
  }
}

// https://blog.campvanilla.com/reactjs-dropdown-menus-b6e06ae3a8fe
class ReviewsCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showMenu: false,
    };

    this.showMenu = this.showMenu.bind(this);
    this.closeMenu = this.closeMenu.bind(this);
    this.renderReviews = this.renderReviews.bind(this);
  }

  renderReviews(reviews=[]) {
    return reviews.map(function(review) {
      return (
        <div>
        <h4>{review.first_name} said:</h4>
        <h5>{review.rating}/5</h5>
        <p>{review.description}</p>
        </div>
      )
    })
  }

  showMenu(event) {
    event.preventDefault();
    this.setState({ showMenu: true }, () => {
      document.addEventListener('click', this.closeMenu);
    });
  }

  closeMenu(event) {
    if (!this.dropdownMenu.contains(event.target)) {
      this.setState({ showMenu: false }, () => {
        document.removeEventListener('click', this.closeMenu);
      });
    }
  }

  render() {
    return (
      <div className="profilebtn" >
        <button onClick={this.showMenu}>
          Reviews
        </button>
        { this.state.showMenu ? (
          <div className="menu"
            ref={ (element) => { this.dropdownMenu = element } }>
            {this.renderReviews(this.props.reviews)}
          </div>
          ) : (
          null )}
      </div>
    );
  }
}

class Profile extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      artist: {},
      photoView: 'featured'
    }
    this.addCarouselPhotos = this.addCarouselPhotos.bind(this);
    this.areFeaturedPhotos = this.areFeaturedPhotos.bind(this);
    this.showPortfolio = this.showPortfolio.bind(this)
    this.showFeatures = this.showFeatures.bind(this)
  }

  areFeaturedPhotos(photos=[]) {
    return photos.filter(photo => photo.featured.includes("true"));
  }

  addCarouselPhotos(photos=[]) {
    let filteredPhotos = this.areFeaturedPhotos(photos);

    return filteredPhotos.map(function(photo) {
      return (
        <div className="sliderImg" >
          <img alt="900x500" src={photo.src} />
        </div>
        )
    });
  }

  showPortfolio = () => {
    this.setState({photoView: "portfolio"})
  }

  showFeatures = () => {
    this.setState({photoView: "featured"})
  }

  componentDidMount() {

    const { id } = this.props.match.params;

    this.setState({artistId: id});

    axios.get(`/artists/${ id }`, {
      params: {
        artistId: id
      }
    })
    .then((res) => {
      let collection = res.data.images;
      let packages = res.data.packages;
      let bio = res.data.images[0].bio;
      let fullName = res.data.images[0].first_name + ' ' + res.data.images[0].last_name;
      let avatarImage = res.data.images[0].profile_image;
      let twitter = res.data.images[0].twitter_url;
      let facebook = res.data.images[0].facebook_url;
      let instagram = res.data.images[0].instagram_url;
      let reviews = res.data.reviews;

      this.setState({
        redirect: true,
        twitter: twitter,
        facebook: facebook,
        instagram: instagram,
        fullName: fullName,
        avatarImage: avatarImage,
        packages: packages,
        collection: collection,
        bio: bio,
        reviews: reviews});
    });
  }

  render() {

    const settings = {
      infinite: true,
      centerMode: true,
      slidesToShow: 2,
      slidesToScroll: 1,
      rows: 1,
      autoplay: true,
      focusOnSelect: true,
    };

    return (
      <div className="profile">
        <Avatar name={ this.state.fullName }
                avatar={ this.state.avatarImage }/>
        <ProfileDesc bio={this.state.bio}/>
        <SocialMedia twitter={this.state.twitter}
                     facebook={this.state.facebook}
                     instagram={this.state.instagram}/>
        <div className="featuredPortfolio">
          <button onClick={this.showPortfolio}>
            View Portfolio
          </button>
          <button onClick={this.showFeatures}>
            Featured Photos
          </button>
          {this.state.photoView === 'featured' ? (
            <div>
              <Slider {...settings} >
                {this.addCarouselPhotos(this.state.collection)}
              </Slider>
              <br />
            </div>
            ) : (
            <div>
              <h1>Portfolio Photos:</h1>
              <Portfolio artistPhotos={this.state.collection} />
            </div>
            )}
        </div>
        <AvailabilityCard currentUser={this.propscurrentUser}
                          disabledDays={this.state.disabledDays}
                          artistId={this.state.artistId}/>
        <PackagesCard packages={this.state.packages}/>
        <ReviewsCard reviews={this.state.reviews} />
      </div>
    )
  }
}

export default Profile;
