import React from 'react';

// https://blog.campvanilla.com/reactjs-dropdown-menus-b6e06ae3a8fe
class EditPackagesCard extends React.Component {
  constructor(props) {
    super(props);

    this.renderPricePackage = this.renderPricePackage.bind(this);
  }

  renderPricePackage(pricePackages=[]) {
    let tier;

    return pricePackages.map(function(pricePackage) {
      if (pricePackage.tier === 1) {
      tier = "Basic";
    } else if (pricePackage.tier === 2) {
      tier = "Intermediate";
    } else if (pricePackage.tier === 3) {
      tier = "Deluxe";
    }
      return (
        <div>
          <label>{tier}</label>
          <input type="number" name="packageprice" value={pricePackage.price}></input>
          <label>Description</label>
          <textarea name="packagedescription" value={pricePackage.description} placeholder="Describe expected services, deliverables, and occasion recommendations."/>
        </div>
        )
    })
  }

  render() {
    return (
      <div className="profilebtn" >
        <button>
          Packages
        </button>
        <div className="menu"
          ref={ (element) => { this.dropdownMenu = element } }>
          {this.renderPricePackage(this.props.packages)}
        </div>
      </div>
    );
  }
}

export default EditPackagesCard;
